import { Box, Button, FormControl, FormLabel, Input, InputGroup, InputRightElement, StackDivider, VStack } from '@chakra-ui/react'
import React, { useState } from 'react'

export default function SignUp() {

    const [show, setshow] = useState(false)

    const [name, setname] = useState('')
    const [email, setemail] = useState('')
    const [pass, setpass] = useState('')
    const [confpass, setconfpass] = useState('')
    const [pic, setpic] = useState();


    return (
        <>
            <VStack divider={<StackDivider borderColor='gray.200' />} spacing="5px" align='stretch'>
                <FormControl id="name">
                    <FormLabel>Name</FormLabel>
                    <Input placeholder='Enter Name' onChange={(e) => {
                        setname(e.target.value)
                    }}></Input>
                </FormControl>
                <FormControl id="email">
                    <FormLabel>Email</FormLabel>
                    <Input placeholder='Enter Email' onChange={(e) => {
                        setemail(e.target.value)
                    }}></Input>
                </FormControl>


                <FormControl id='password' isRequired>
                    <FormLabel>Password</FormLabel>
                    <InputGroup>
                        <Input type={show ? "text" : "password"} placeholder='Enter Pass' onChange={(e) => {
                            setpass(e.target.value)
                        }}></Input>
                        <InputRightElement>
                            <Button onClick={() => {
                                show ? setshow(false) : setshow(true);
                            }}>
                            </Button>
                        </InputRightElement>
                    </InputGroup>
                </FormControl>
                <FormControl id='confirm_password' isRequired>
                    <FormLabel>Confirm Password</FormLabel>
                    <InputGroup>
                        <Input type="password" placeholder='Enter Pass' onChange={(e) => {
                            setpass(e.target.value)
                        }}></Input>
                    </InputGroup>
                </FormControl>

                <FormControl>
                    <FormLabel>
                        <Input type="file" accept='image/' onChange={(e) => {
                            postDetails(e.target.files[0]);
                        }}>
                        </Input>
                    </FormLabel>
                </FormControl>
            </VStack>
        </>
    )
}
