import { Box, Button, FormControl, FormLabel, Input, InputGroup, InputRightElement, StackDivider, VStack } from '@chakra-ui/react'
import React, { useState } from 'react'


export default function Login() {

    const [show, setshow] = useState(false)

    const [email, setemail] = useState('')
    const [pass, setpass] = useState('')
    const history = useHistory();
    const toast = useToast();

    const submitHandler = async() => {
        setLoading(true);
        if (!email || !password) {
          toast({
            title: "Please Fill all the Feilds",
            status: "warning",
            duration: 5000,
            isClosable: true,
            position: "bottom",
          });
          setLoading(false);
          return;
        }

    }

    return (
        <VStack divider={<StackDivider borderColor='gray.200' />} spacing="5px" align='stretch'>

            <FormControl id="_email">
                <FormLabel>Email</FormLabel>
                <Input placeholder={email} onChange={(e) => {
                    setemail(e.target.value)
                }}></Input>
            </FormControl>


            <FormControl id='password' isRequired>
                <FormLabel>Password</FormLabel>
                <InputGroup>
                    <Input placeholder={pass} type={show ? "text" : "password"} onChange={(e) => {
                        setpass(e.target.value)
                    }}></Input>
                    <InputRightElement>
                        <Button onClick={() => {
                            show ? setshow(false) : setshow(true);
                        }}>
                        </Button>
                    </InputRightElement>
                </InputGroup>
            </FormControl>


            <Button onClick={submitHandler}>
                Login
            </Button>
            <Button onClick={() => {
                setemail("guest@example.com");
                setpass("12345");
            }}>
                Click to guest User
            </Button>
        </VStack>

    )
}
