import { Box, Button, FormControl, Input, Modal, ModalBody, ModalCloseButton, ModalContent, ModalFooter, ModalHeader, ModalOverlay, Toast, useDisclosure, useToast } from '@chakra-ui/react'
import axios from "axios";
import UserListItem from "../UserAvtar/UserListItem";
import React, { useState } from 'react'
import { ChatState } from '../../Context/chatProvider';
import UserBadegeItem from '../UserAvtar/UserBadegeItem';

export default function UpdateGroupChatModal({ fetchAgain, setfetchAgain }) {

    const { isOpen, onOpen, onClose } = useDisclosure();

    const [groupChatName, setGroupChatName] = useState("");
    const [search, setSearch] = useState("");
    const [searchResult, setSearchResult] = useState([]);
    const [loading, setLoading] = useState(false);
    const [renameloading, setrenameLoading] = useState(false)

    const toast = useToast();

    const { user, selectedChat, setSelectedChat } = ChatState();


    const handleRemove = () => {

    }

    const handleRename = async () => {
        console.log("updates")
        if (!groupChatName) {
            return;
        }

        try {
            setrenameLoading(true);

            const config = {
                headers: {
                    Authorization: `Bearer ${user.token}`,
                },
            };

            const { data } = await axios.put(`/api/chat/rename`,
                {
                    chatId: selectedChat._id,
                    chatName: groupChatName,
                },
                config
            );

            // console.log(data);


            setSelectedChat(data);
            setfetchAgain(!fetchAgain);
            setrenameLoading(false);


        } catch (error) {
            toast({
                title: "Error Occured!",
                description: error.response.data.message,
                status: "error",
                duration: 5000,
                isClosable: true,
                position: "bottom",
            });
            setrenameLoading(false);
        }
        setGroupChatName("");
    }

    const handleSearch = async (query) => {

        setSearch(query);
        if (!query) {
            return;
        }

        try {

            setLoading(true);

            const config = {
                headers: {
                    Authorization: `Bearer ${user.token}`,
                },
            };

            const { data } = await axios.get(`/api/user?search=${search}`, config);
            // console.log(data);
            setLoading(false);
            setSearchResult(data);

        } catch (error) {
            toast({
                title: "Error Occured!",
                description: "Failed to Load the Search Results",
                status: "error",
                duration: 5000,
                isClosable: true,
                position: "bottom-left",
            });
        }

    }

    const handleAddUser = async (AddUser) => {
        if (selectedChat.includes(userToAdd)) {
            toast({
                title: "Error ",
                description: "Already Exist user",
                status: "error",
                duration: 5000,
                isClosable: true,
                position: "bottom-left",
            });
            return;
        }

        // setSelectedUsers([...selectedUsers, userToAdd]);

    }

    return (
        <>
            <Button onClick={onOpen}>Open Modal</Button>

            <Modal isOpen={isOpen} onClose={onClose}>
                <ModalOverlay />
                <ModalContent>
                    <ModalHeader>{selectedChat.chatName}</ModalHeader>
                    <ModalCloseButton />
                    <ModalBody>

                        <Box w="100%" d="flex" flexWrap="wrap" pb={3}>
                            {selectedChat.user.map((u) => (
                                <UserBadegeItem
                                    key={u._id}
                                    user={u}
                                    admin={selectedChat.groupAdmin}
                                    handleFunction={() => handleRemove(u)}
                                />
                            ))}
                        </Box>

                        <FormControl d="flex">
                            <Input
                                placeholder="Chat Name"
                                mb={3}
                                value={groupChatName}
                                onChange={(e) => setGroupChatName(e.target.value)}
                            />
                            <Button
                                variant="solid"
                                colorScheme="teal"
                                ml={1}
                                isLoading={renameloading}
                                onClick={handleRename}
                            >
                                Update
                            </Button>
                        </FormControl>

                        <FormControl>
                            <Input
                                placeholder="Add User to group"
                                mb={1}
                                onChange={(e) => handleSearch(e.target.value)}
                            />
                        </FormControl>

                        {
                            loading ? <div>Loading</div> : (
                                searchResult?.slice(0, 4).map((user) => (
                                    <UserListItem key={user._id} user={user} handleFunction={() => handleAddUser(user)} />
                                ))
                            )
                        }

                    </ModalBody>

                    <ModalFooter>
                        <Button onClick={() => handleRemove(user)} colorScheme="red">
                            Leave Group
                        </Button>
                    </ModalFooter>
                </ModalContent>
            </Modal>
        </>
    )
}
