import { Box, FormControl, FormLabel, Input, StackDivider, VStack } from '@chakra-ui/react'
import React, { useState } from 'react'

export default function SignUp() {

    const [name, setname] = useState('')
    const [email, setemail] = useState('')
    const [pass, setpass] = useState('')
    const [confpass, setconfpass] = useState('')
    const [pic, setpic] = useState();

    return (
        <>
            <VStack divider={<StackDivider borderColor='gray.200' />} spacing="5px" align='stretch'>
                <FormControl>
                    <FormLabel>Name</FormLabel>
                    <Input placeholder='Enter Name' onChange={(e)=>{
                        setname(e.target.value)
                    }}></Input>
                </FormControl>
                <FormControl>
                    <FormLabel>Email</FormLabel>
                    <Input placeholder='Enter Email' onChange={(e)=>{
                        setemail(e.target.value)
                    }}></Input>
                </FormControl>
                <FormControl>
                    <FormLabel>Password</FormLabel>
                    <Input placeholder='Enter Pass' onChange={(e)=>{
                        setname(e.target.value)
                    }}></Input>
                </FormControl>
            </VStack>
        </>
    )
}
